import React, {useState, useEffect, useCallback} from 'react';
import {Button, Container, EmptyLayout, Icon} from "@yosmy/ui";
import {ServiceProvider, useService} from "@yosmy/service";
import {
    Amount,
    AmountSelector,
    Country,
    CountrySelector,
    Provider,
    ProviderSelector,
} from "../"

const Integration = () => {
    const [country, setCountry] = useState(null);
    const [provider, setProvider] = useState(null);
    const [amount, setAmount] = useState(null);

    const icons = useService("icons");

    const StepLayout = useCallback(({children, onCancel}) => {
        return <Container
            flow="row"
            align={{
                main: "space-between",
                cross: "center"
            }}
        >
            {children}
            <Button
                type="icon"
                onClick={onCancel}
            >
                <Icon data={icons.actions.close} />
            </Button>
        </Container>
    }, [])

    useEffect(() => {
        if (country === null) {
            setProvider(null)
            setAmount(null)
        }

        if (provider === null) {
            setAmount(null)
        }
    }, [country, provider])

    return <Container>
        {country === null
            ? <CountrySelector
                templates={{
                    layout: ({input}) => {
                        return <EmptyLayout>
                            {input}
                        </EmptyLayout>
                    },
                }}
                favorites={["CU", "US"]}
                onSelect={(code) => setCountry(code)}
            />
            : <StepLayout
                onCancel={() => setCountry(null)}
            >
                <Country
                    code={country}
                />
            </StepLayout>
        }
        {country !== null && (provider === null
            ? <ProviderSelector
                templates={{
                    layout: ({input}) => {
                        return <EmptyLayout>
                            {input}
                        </EmptyLayout>
                    },
                }}
                onSelect={(provider) => setProvider(provider)}
            />
            : <StepLayout
                onCancel={() => setProvider(null)}
            >
                <Provider
                    name={provider.name}
                    logo={provider.logo}
                />
            </StepLayout>
        )}
        {provider !== null && (amount === null
            ? <AmountSelector
                templates={{
                    layout: ({input}) => {
                        return <EmptyLayout>
                            {input}
                        </EmptyLayout>
                    },
                }}
                provider={provider.code}
                onSelect={(amount) => setAmount(amount)}
            />
            : <StepLayout
                onCancel={() => setAmount(null)}
            >
                <Amount
                    send={amount.send}
                    receive={amount.receive}
                    currency={amount.currency}
                />
            </StepLayout>
        )}
    </Container>
}

export default {
    title: 'Integration',
    component: Integration,
};

const Template = () => {
    return <ServiceProvider services={[
        {id: "api", value: {
            collectCountries: async () => {
                return new Promise(function(resolve) {
                    setTimeout(() => {
                        resolve([
                            "BR", "CO", "CU", "EC", "US"
                        ]);
                    }, 1000);
                });
            },
            collectProviders: async () => {
                    return new Promise(function(resolve) {
                        setTimeout(() => {
                            resolve([
                                {
                                    code: "x",
                                    name: "Provider X",
                                    logo: {
                                        data: "data:image/gif;base64,",
                                        width: 25,
                                        height: 25
                                    },
                                },
                                {
                                    code: "y",
                                    name: "Provider Y",
                                    logo: {
                                        data: "data:image/gif;base64,",
                                        width: 25,
                                        height: 25
                                    }
                                }
                            ]);
                        }, 1000);
                    });
            },
            gatherProvider: async () => {
                    return new Promise(function(resolve) {
                        setTimeout(() => {
                            resolve({
                                code: "x",
                                name: "Provider X",
                                logo: {
                                    data: "data:image/gif;base64,",
                                    width: 25,
                                    height: 25
                                },
                            });
                        }, 1000);
                    });
            },
            collectAmounts: async () => {
                    return new Promise(function(resolve) {
                        setTimeout(() => {
                            resolve([
                                {
                                    product: "GY_DC_TopUp",
                                    send: 5,
                                    receive: 1030,
                                    currency: "GYD"
                                },
                                {
                                    product: "GY_DC_TopUp",
                                    send: 10,
                                    receive: 2060,
                                    currency: "GYD"
                                },
                            ]);
                        }, 1000);
                    });
            },
            gatherAmount: async () => {
                return new Promise(function(resolve) {
                    setTimeout(() => {
                        resolve({
                            product: "GY_DC_TopUp",
                            send: 5,
                            receive: 1030,
                            currency: "GYD"
                        });
                    }, 1000);
                });
            }
        }},
    ]}>
        <Integration />
    </ServiceProvider>
};

export const Default = Template.bind({});

